mapper.update:
update message_record
set msg_status = #{newStatus}
where id = #{id}
  and msg_status = #{oldStatus};

update message_record
set msg_status = #{newStatus}
where business_id = #{businessId}
  and business_type = #{businessType}
  and msg_status = #{oldStatus};

update message_record
set retries_number = retries_number + 1
where id = #{messageId};

mapper.select:
select

from message_record
where business_id = #{businessId}
and business_type = #{businessType}
